import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { createpost } from "../redux/actions/post_action";

function Addbuild() {
  const [buildname, setBuildname] = useState("");
  const [backgroundimage, setBackgroundimage] = useState("");
  const [description, setDescription] = useState("");
  const [photos, setPhotos] = useState("");
  const [partslist, setPartslist] = useState("");
  const dispatch = useDispatch();
  const history = useHistory();
  const handleCreatebuild = (e) => {
    e.preventDefault();
    dispatch(createpost({ buildname, description, partslist, photos, backgroundimage }, history));
  };
  return (
    <div>
      <label>Build Name</label>
      <input
        type='text'
        placeholder='Enter your build name'
        name='bname'
        required
        value={buildname}
        onChange={(e) => setBuildname(e.target.value)}
      />
      <form action='/profile' method='post' enctype='multipart/form-data'>
        <input type='file' onChange={(e) => setBackgroundimage(e.target.files[0])} />
      </form>

      <label>Description</label>
      <input
        type='text'
        placeholder='tell us more about your build'
        required
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <label>Photos</label>
      <form action='/profile' method='post' enctype='multipart/form-data'>
        <input type='file' multiple onChange={(e) => setPhotos(e.target.files)} />
      </form>
      <label for='PList'>
        <b className='signTextName'>Parts List</b>
      </label>
      <input
        type='text'
        placeholder='you can put links to the parts used in your build'
        name='Description'
        required
        value={partslist}
        onChange={(e) => setPartslist(e.target.value)}
      />

      <button onClick={handleCreatebuild}>ADD</button>

      <Link to='/profile'>
        <button>Cancel</button>
      </Link>
    </div>
  );
}

export default Addbuild;
