import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { createpost } from "../redux/actions/post_action";

function Addbuild() {
  const [buildname, setBuildname] = useState("");
  //const [backgroundimage, setBackgroundimage] = useState("");
  //const [description, setDescription] = useState("");
  //const [photos, setPhotos] = useState("");
  //const [partslist, setPartslist] = useState("");
  //const dispatch = useDispatch();
  const handleCreatebuild = (e) => {
    e.preventDefault();
    dispatch(createpost({ buildname }));
  };
  return (
    <div className='addbuild'>
      <label for='Bname'>
        <b className='signTextName'>Build Name</b>
      </label>
      <input
        type='text'
        placeholder='Enter your build name'
        name='bname'
        required
        value={buildname}
        onChange={(e) => setBuildname(e.target.value)}
      />

      <form action='/profile' method='post' enctype='multipart/form-data'>
        <input type='file' name='avatar' />
      </form>

      <label for='Description'>
        <b className='signTextName'>Description</b>
      </label>
      <input type='text' placeholder='tell us more about your build' name='Description' required />
      <label for='Photos'>
        {" "}
        <b className='signTextName'>Photos</b>
      </label>
      <label for='PList'>
        <b className='signTextName'>Parts List</b>
      </label>
      <input type='text' placeholder='you can put links to the parts used in your build' name='Description' required />
      <button onClick={handleCreatebuild}>ADD</button>
      <button>Cancel</button>
    </div>
  );
}

export default Addbuild;
