            placeholder='Enter Username'
            name='uname'
            required
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <label for='uname'>
            <b className='signTextName'>Email</b>
          </label>
          <input
            type='text'
            placeholder='Enter Email'
            name='uname'
            required
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />

          <label for='psw'>
            <b className='signTextName'>Password</b>
          </label>
          <input
            type='password'
            placeholder='Enter Password'
            name='psw'
            required
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <label for='psw'>
            <b className='signTextName'>Confirm Password</b>
          </label>
          <input
            type='password'
            placeholder='Confirm Password'
            name='psw'
            required
            value={passworda}
            onChange={(e) => setPassworda(e.target.value)}
          />
          <button type='submit' onClick={handleSignUp}>
            Sign Up
          </button>
