import axios from "axios";
import { CREATE_POST, CURRENT_POST, LIST_POST, POST_ERROR } from "../types/post_types";

export const createpost = (formdata) => async (dispatch) => {
  try {
    const res = await axios.post("/api/posts/CreatePost", formdata);
    dispatch({ type: CREATE_POST, payload: res.data });
  } catch (error) {
    dispatch({ type: POST_ERROR, payload: error.response.data });
  }
};

export const listpost = () => async (dispatch) => {
  try {
    const res = await axios.get("/api/posts/ListPost");
    dispatch({ type: LIST_POST, payload: res.data.Posts });
    //console.log(res.data.Posts);
  } catch (error) {
    console.log(error);
  }
};
export const currentPost = () => async (dispatch) => {
  const config = {
    headers: {
      Authorization: localStorage.getItem("token"),
    },
  };
  try {
    const res = await axios.get("/api/posts/ListUserPost");
    dispatch({ type: CURRENT_POST, payload: res.data.Posts });
    console.log(res.data.Posts);
  } catch (error) {
    console.log(error);
  }
};
