import axios from "axios";
import { CREATE_POST, POST_ERROR } from "../types/post_types";

export const createpost = (formdata) => async (dispatch) => {
  try {
    const res = await axios.post("/api/posts/CreatePost");
    dispatch({ type: CREATE_POST, payload: res.data, formdata });
  } catch (error) {
    dispatch({ type: POST_ERROR, payload: error.response.data });
  }
};
