import axios from "axios";
import { CREATE_POST, LIST_POSTS, POST_ERROR } from "../types/post_types";

export const createpost = (formdata) => async (dispatch) => {
  try {
    const res = await axios.post("/api/posts/CreatePost", formdata);
    dispatch({ type: CREATE_POST, payload: res.data });
  } catch (error) {
    dispatch({ type: POST_ERROR, payload: error.response.data });
  }
};

export const listpost = () => async (dispatch) => {
  try {
    const res = await axios.get("/api/posts/ListPost");
    dispatch({ type: LIST_POSTS, payload: res.data.Posts });
  } catch (error) {
    dispatch({ type: LIST_POSTS, payload: error.response.data });
  }
};
