import axios from "axios";
import { AUTH_ERRORS, CURRENT, LOGOUT, USER_LOADING, USER_LOGIN, USER_REGISTER } from "../types/authTypes";
export const register = (formData, history) => async (dispatch) => {
  dispatch({ type: USER_LOADING });
  try {
    const res = await axios.post("/api/users/SignUp", formData);
    dispatch({
      type: USER_REGISTER,
      payload: res.data,
    });
    await history.push("/profile");
  } catch (error) {
    dispatch({
      type: AUTH_ERRORS,
      payload: error.response.data,
    });
  }
};
export const login = (formData, history) => async (dispatch) => {
  dispatch({ type: USER_LOADING });
  try {
    const res = await axios.post("/api/users/SignIn", formData);
    dispatch({
      type: USER_LOGIN,
      payload: res.data,
    });
    await history.push("/profile");
  } catch (error) {
    dispatch({
      type: AUTH_ERRORS,
      payload: error.response.data,
    });
  }
};

export const current = () => async (dispatch) => {
  const config = {
    headers: {
      Authorization: localStorage.getItem("token"),
    },
  };
  try {
    const res = await axios.get("/api/users/Current", config);
    dispatch({ type: CURRENT, payload: res.data });
    console.log(res.data);
  } catch (error) {
    console.log(error);
  }
};
export const signout = () => {
  return { type: LOGOUT };
};
export const deleteUser = () => {
  return;
};
