const { CREATE_POST, POST_ERROR, LIST_POST } = require("../types/post_types");
const initialstate = {
  newPost: null,
  msg: null,
  errors: null,
  posts: [],
};
const postReducer = (state = initialstate, action) => {
  switch (action.type) {
    case CREATE_POST:
      return {
        ...state,

        newPost: action.payload.newPost,
      };
    case POST_ERROR:
      return {
        ...state,
        errors: action.payload.errors,
      };
    case LIST_POST:
      return {
        ...state,
        newPost: action.payload.newpost,
      };
    default:
      return state;
  }
};
export default postReducer;
