import {
  USER_LOGIN,
  USER_REGISTER,
  GET_AUTH_USER,
  USER_LOADING,
  LOGOUT,
  AUTH_ERRORS,
} from "../types/authTypes";
const initialstate = {
  isAuth: false,
  loading: false,
  user: null,
  token: localStorage.getItem("token"),
  msg: null,
};

const authReducer = (state = initialstate, action) => {
  switch (action.type) {
    case USER_REGISTER:
      return {
        ...state,
        ...action.payload,
        loading: false,
        error: null,
        isAuth: true,
      };

    default:
      return state;
  }
};

export default authReducer;
