import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { editedBuild } from "../redux/actions/post_action";

function Editbuild() {
  const editedbuild = useSelector((state) => state.postReducer.post);
  const [buildname, setBuildname] = useState(editedbuild.buildname);
  const [backgroundimage, setBackgroundimage] = useState(editedbuild.backgroundimage);
  const [description, setDescription] = useState("");
  const [photos, setPhotos] = useState("");
  const [partslist, setPartslist] = useState("");
  const dispatch = useDispatch();
  const history = useHistory();
  const handleEditbuild = (e) => {
    e.preventDefault();
    dispatch(
      editedBuild(
        editedbuild._id,
        { buildname, description, partslist, photos, backgroundimage },
        history.push("/builds")
      )
    );
  };
  /* useEffect(() => {
    setBuildname();
    setBackgroundimage(editedbuild.backgroundimage);
    setDescription(editedbuild.description);
    setPhotos(editedbuild.photos);
    setPartslist(editedbuild.partslist);
  }, []);*/
  return (
    <div>
      <div className='addbuildHead'>
        <h1 className='addbuildTitles'>EDIT YOUR BUILD</h1>
      </div>
      <h4 className='addbuildInfoInput'>Build Photos</h4>
      <div className='addbuildPhotos'></div>
      <p className='addbuildDragText'>Drag your kwad build photos here or click in this area</p>
      <form action='/profile' method='post' enctype='multipart/form-data'>
        <input type='file' name='avatar' multiple onChange={(e) => setPhotos(e.target.files)} />
      </form>
      <input
        type='text'
        placeholder='Enter your build name'
        name='bname'
        required
        value={buildname}
        onChange={(e) => setBuildname(e.target.value)}
      />

      <form action='/profile' method='post' enctype='multipart/form-data'>
        <input type='file' name='avatar' onChange={(e) => setBackgroundimage(e.target.files[0])} />
      </form>

      <label for='Description'>
        <b className='signTextName'>Description</b>
      </label>
      <input
        type='text'
        placeholder='tell us more about your build'
        name='Description'
        required
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <label for='Photos'>
        <b className='signTextName'>Photos</b>
      </label>

      <label for='PList'>
        <b className='signTextName'>Parts List</b>
      </label>
      <input
        type='text'
        placeholder='you can put links to the parts used in your build'
        name='Description'
        required
        value={partslist}
        onChange={(e) => setPartslist(e.target.value)}
      />
      <button onClick={handleEditbuild}>Edit</button>
      <Link to='/profile'>
        <button>Cancel</button>
      </Link>
    </div>
  );
}

export default Editbuild;
