const { CREATE_PSOT } = require("../types/post_types");
const initialstate = {
  newPost: null,
  msg: null,
  errors: null,
};
const postReducer = (state = initialstate, action) => {
  switch (action.type) {
    case CREATE_PSOT:
      return {
        ...state,
        errors: action.payload.errors,
        ...action.payload,
        newPost: action.payload.newPost,
      };
    default:
      return state;
  }
};
export default postReducer;
